con:config.script:
String lang = "ru"
String textsToCheck = "лета"
String suggestion = "лето"

if (testRunner.testCase.getPropertyValue("lang") == "")
{
	testRunner.testCase.setPropertyValue("lang", lang)
	testRunner.testCase.setPropertyValue("textToCheck", textsToCheck)
	testRunner.testCase.setPropertyValue("expectedSuggestion", suggestion)
}

else if (testRunner.testCase.getPropertyValue("lang") == "ru")
{
	lang = "uk"
	textsToCheck = "лето"
	suggestion = "літо"
	testRunner.testCase.setPropertyValue("lang", lang)
	testRunner.testCase.setPropertyValue("textToCheck", textsToCheck)
	testRunner.testCase.setPropertyValue("expectedSuggestion", suggestion)
}

else if (testRunner.testCase.getPropertyValue("lang") == "uk")
{
	lang = "en"
	textsToCheck = "sumer"
	suggestion = "summer"
	testRunner.testCase.setPropertyValue("lang", lang)
	testRunner.testCase.setPropertyValue("textToCheck", textsToCheck)
	testRunner.testCase.setPropertyValue("expectedSuggestion", suggestion)
}

else if (testRunner.testCase.getPropertyValue("lang") == "en")
{
	testRunner.gotoStepByName("TearDown")
}
